import React, { useState } from 'react'


export default function PostCreate({ createPost, currentUser }) {
  const [formData, setFormData] = useState({
    title: "",
    body: "",
    author: currentUser.username,
    thumbnail: ""
  });
  const { title, body, thumbnail } = formData;
  console.log(formData)

  const handleChange = (e) => {

    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value,
    }));
  }
  console.log(formData)

  return (
      <div className="form-container">
        <form onSubmit={(e) => {
          e.preventDefault()
          createPost(formData);
        }}>
          <h3>Create a post</h3>
          <label>Title:
            <input
              type="text"
              name="title"
              value={title}
              onChange={handleChange}
            />
          </label>
          <label>Body:
            <input
              type="text"
              name="body"
              value={body}
              onChange={handleChange}
            />
          </label>
          <button>Submit</button>
        </form>


        <div className="login-box" id="register-box">
          <h2 className="form-title">Register</h2>
    <form >

    <div class="user-box">

      <input
      id="input"
      type='text'
      name='title'
      value={title}
      onChange={handleChange}
      />
              <label id='login-label'>
      Title:
      </label>
      </div>

      <div class="user-box">
      <input
      id="input"
      type='text'
      name='thumbnail'
      value={thumbnail}
      onChange={handleChange}
      />
              <label id='login-label'>
      Thumbnail (url):
      </label>
      </div>

      <div class="user-box">
      <textarea 
      id="input"
      type='text'
      name='body'
      rows="8"
      value={body}
      onChange={handleChange}
      />
              <label id='login-label'>
      Bio:
      </label>
      </div>

      <a onClick={(e)=> {
        e.preventDefault();
        handleRegister(formData);
        }}>
      <span></span>
      <span></span>
      <span></span>
      <span></span>
      Submit
    </a>
    </form>
    </div>
      </div>
  )
}
