import { useState } from 'react';
import './Register.css'


export default function Register(props) {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    profile_pic: '',
    bio: '',
    interests: ['hello','non']
  });
  const { username, email, password, profile_pic, bio, interests } = formData;
  const { handleRegister } = props;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  return (
    <div className="login-box">
          <h2 className="form-title">Register</h2>
    <form >

    <div class="user-box">

      <input
      id="input"
      type='text'
      name='username'
      value={username}
      onChange={handleChange}
      />
              <label id='login-label'>
      Username:
      </label>
      </div>

      <div class="user-box">
      <input
      id="input"
      type='text'
      name='email'
      value={email}
      onChange={handleChange}
      />
              <label id='login-label'>
      Email:
      </label>
      </div>

      <div class="user-box">
      <input
      type='password'
      name='password'
      value={password}
      onChange={handleChange}
      />
              <label id='login-label'>
      Password:
      </label>
      </div>

      <div class="user-box">
      <input
      id="input"
      type='text'
      name='profile_pic'
      value={profile_pic}
      onChange={handleChange}
      />
              <label id='login-label'>
      Profile Pic:
      </label>
      </div>

      <div class="user-box">
      <input
      id="input"
      type='text'
      name='interests'
      value={interests}
      onChange={handleChange}
      />
              <label id='login-label'>
      Interests:
      </label>
      </div>

      <div class="user-box">
      <textarea 
      id="input"
      type='text'
      name='bio'
      cols="3" rows="10"
      value={bio}
      onChange={handleChange}
      />
              <label id='login-label'>
      Bio:
      </label>
      </div>

      <a onClick={(e)=> {
        e.preventDefault();
        handleRegister(formData);
        }}>
      <span></span>
      <span></span>
      <span></span>
      <span></span>
      Submit
    </a>
    </form>
    </div>
  );
}
