import { useState } from 'react';

export default function Register(props) {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    profile_pic: '',
    bio: '',
    interests: ['hello','non']
  });
  const { username, email, password, profile_pic, bio, interests } = formData;
  const { handleRegister } = props;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  return (
    <div>
          <h2 className="form-title">Register</h2>
    <form >

    <div class="user-box">

      <input
      type='text'
      name='username'
      value={username}
      onChange={handleChange}
      />
              <label id='login-label'>
      Username:
      </label>
      </div>

      <div class="user-box">
      <input
      type='password'
      name='password'
      value={password}
      onChange={handleChange}
      />
              <label id='login-label'>
      Password:
      </label>
      </div>

      <div class="user-box">
      <input
      type='text'
      name='email'
      value={email}
      onChange={handleChange}
      />
              <label id='login-label'>
      Password:
      </label>
      </div>

      <div class="user-box">
      <input
      type='password'
      name='password'
      value={password}
      onChange={handleChange}
      />
              <label id='login-label'>
      Password:
      </label>
      </div>


      <br />
      <label>
        Password:
        <input
          type='text'
          name='password'
          value={password}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        profile_pic:
        <input
          type='text'
          name='profile_pic'
          value={profile_pic}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        bio:
        <input
          type='text'
          name='bio'
          value={bio}
          onChange={handleChange}
        />
      </label>
      <br />
      <label>
        interests:
        <input
          type='text'
          name='interests'
          value={interests}
          onChange={handleChange}
        />
      </label>
      <br />
      <a onClick={(e)=> {
        e.preventDefault();
        handleRegister(formData);
        }}>
      <span></span>
      <span></span>
      <span></span>
      <span></span>
      Submit
    </a>
    </form>
    </div>
  );
}
